SELECT note_num, receiver, sender, note_date, note_subject, note_state FROM note
	WHERE sender = (SELECT user_id FROM member WHERE user_id = #{param2}) AND sent_hidden=0
		ORDER BY note_num DESC LIMIT 5 OFFSET #{param1}


