mapper.select:

SELECT dates.date as formatted_date, COUNT(reserve.r_num) as reservation_count
FROM (
    SELECT DISTINCT DATE_ADD(MIN(DATE(r_start)), INTERVAL a.a DAY) as date
    FROM reserve
    CROSS JOIN (
        SELECT 0 as a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4
    ) as a
    WHERE com_id = #{param1}
    GROUP BY a.a
) as dates
LEFT JOIN reserve ON DATE(dates.date) BETWEEN DATE(reserve.r_start) AND DATE(reserve.r_end) AND reserve.com_id = #{param1}
GROUP BY dates.date
ORDER BY dates.date;
